mapper.select:
select
id, nickname, evaluation, reg_date, category, subject, meet_date, location
from
(select *, row_number() over (order by id) as num from board natural join user b1
where not view_option = 0
and location like '%'||#{location}||'%' and between #{minTime} and #{maxTime} and category = #{category}
) as
b2
where
num between #{startIndex} and #{endIndex}
